    mdxType: "MDXLayout"
  }), mdx("hr", null), mdx("h1", null, "Korzysci dla Polski z przynaleznosci do UE"), mdx("p", null, "3 korzy\u015Bci z przynale\u017Cno\u015Bci PL do UE w 2020 & EVER"), mdx("p", null, mdx("a", _extends({
    parentName: "p"
